import { Component, Prop, Vue } from "vue-property-decorator";
import { timestampToTime } from "@/utils/utils";
import { ParamsArchive, ArchiveData } from "@/types/index";

@Component({
  components: {}
})
export default class Archive extends Vue {
  private isLoadEnd: boolean = false;
  private isLoading: boolean = false;
  private articlesList: Array<object> = [];
  private total: number = 0;
  private params: ParamsArchive = {
    keyword: "",
    likes: "", // 是否是热门文章
    state: 1, // 文章发布状态 => 0 草稿，1 已发布,'' 代表所有文章
    article: 1,
    tag_id: "",
    category_id: "",
    pageNum: 1,
    pageSize: 10
  };

  mounted(): void {
    this.handleSearch();
  }

  private async handleSearch(): Promise<void> {
    this.isLoading = true;
    const data: ArchiveData = await this.$https.get(/*this.$urls.getArticleList*/'/articleList1.json', {
      params: this.params
    });
    this.isLoading = false;
    this.articlesList = [...this.articlesList, ...data.list];
    this.total = data.count;
    this.params.pageNum++;
    if (this.total === this.articlesList.length) {
      this.isLoadEnd = true;
    }
  }
}
