import { Vue, Component, Prop, Emit } from "vue-property-decorator";
import { timestampToTime } from "@/utils/utils";
import Comment from "@/components/comment.vue";
import { ToUser } from "@/types/index";

@Component({
  components: {
    Comment
  }
})
export default class CommentList extends Vue {
  @Prop({ default: [] }) list!: Array<object>;
  @Prop({ default: 0 }) numbers!: number;
  @Prop({ default: "" }) article_id!: string;
  private visible: boolean = false;
  private comment_id: string = "";
  private to_user: ToUser = {
    user_id: "",
    name: "",
    avatar: "",
    type: 0
  };

  private handleCancel(): void {
    this.visible = false;
  }

  // @Emit("refreshArticle")
  private handleOk(): void {
    this.visible = false;
    this.$emit("refreshArticle");
  }

  // 添加评论
  private showCommentModal(
    commitId: string,
    user: ToUser,
    secondUser?: ToUser
  ): boolean | void {
    if (!window.sessionStorage.userInfo) {
      this.$message({
        message: "登录才能点赞，请先登录！",
        type: "warning"
      });
      return false;
    }
    // 添加三级评论
    if (secondUser) {
      this.visible = true;
      this.comment_id = commitId;
      this.to_user = user;
    } else {
      // 添加二级评论
      this.visible = true;
      this.comment_id = commitId;
      this.to_user = user;
    }
  }
}
